import React, { useState ,useContext} from 'react';
import {useStateValue} from './../../global/state'
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import PropTypes from 'prop-types';
//import classes from './Layout.css'

import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import MenuIcon from '@material-ui/icons/Menu';
import Drawer from '@material-ui/core/Drawer';
import Tab from '@material-ui/core/Tab';
import Tabs from '@material-ui/core/Tabs';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import Divider from '@material-ui/core/Divider';
import List from '@material-ui/core/List';
import  MainListItems  from './ListItems';
import Button from '@material-ui/core/Button';
import { Link ,withRouter} from 'react-router-dom';
import SwipeableViews from 'react-swipeable-views';

//import Badge from '@material-ui/core/Badge';
//import NotificationsIcon from '@material-ui/icons/Notifications';
//import Container from '@material-ui/core/Container';
//import Grid from '@material-ui/core/Grid';
//import Paper from '@material-ui/core/Paper';

const drawerWidth = 240;
//Inline styles
const useStyles = makeStyles(theme => ({
  root: {
    //display: 'flex',
    //backgroundColor: theme.palette.background.paper,
  },
  toolbar: {
    paddingRight: 24, // keep right padding when drawer closed
  },
  toolbarIcon: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    padding: '0 8px',
    ...theme.mixins.toolbar,
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: 36,
  },
  menuButtonHidden: {
    display: 'none',
  },
  title: {
    flexGrow: 1,
  },
  drawerPaper: {
    position: 'relative',
    whiteSpace: 'nowrap',
    width: drawerWidth,
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  drawerPaperClose: {
    overflowX: 'hidden',
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    width: theme.spacing(7),
    [theme.breakpoints.up('sm')]: {
      width: theme.spacing(9),
    },
  },
  appBarSpacer: theme.mixins.toolbar,
  content: {
    flexGrow: 1,
    height: '100vh',
    overflow: 'auto',
  },
  container: {
    paddingTop: theme.spacing(4),
    paddingBottom: theme.spacing(4),
  },
  paper: {
    padding: theme.spacing(2),
    display: 'flex',
    overflow: 'auto',
    flexDirection: 'column',
  },
  fixedHeight: {
    height: 240,
  },
}));


const LinkTab = (props) => {

  return (
      <Tab
          component="a"
          onClick={event => {
            event.preventDefault();
          }}
          {...props}
      />
  );
}

const TabContainer = ({ children, dir}) => {
  return (
      <Typography component="div" dir={dir} style={{ padding: 8 * 3 }}>
        {children}
      </Typography>
  );
}

TabContainer.propTypes = {
  children: PropTypes.node.isRequired,
  dir: PropTypes.string.isRequired
};



//Funtional Component
const Layout_ = (props) => {
  const classes = useStyles();
  //Hook
  const [open, setOpen] = useState(true);
  const [{ appState ,dataState }, dispatch] = useStateValue();

  const [value,setValue] = useState(0);

  //Handler
  const handleSignOut = () => {
    console.log('sign out ,remove session storage');
    sessionStorage.removeItem("sessionstate");
    props.history.push('/');
    window.location.reload();
  }
  const handleChange = (event, newValue) => {
    console.log('handleChange');
    setValue(newValue);
  }
  const handleChangeIndex = index => {
    console.log('handleChangeIndex');
    setValue(index);
  };


  //const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  return (
    <div className={classes.root}>
      <AppBar position="static" color="default">
        <Tabs value={value} onChange={handleChange} indicatorColor="primary" textColor="primary" variant="fullWidth">
          <Tab label="All" />
          <Tab label="Cooking" />
          <Tab label="Delivering" />
          <Tab label="Cancel" />
        </Tabs>
      </AppBar>
      <main className={classes.content}>
        <SwipeableViews axis={'x'} index={value} onChangeIndex={handleChangeIndex}>
          <TabContainer dir={'rtl'}>Item One</TabContainer>
          <TabContainer dir={'rtl'}>Item Two</TabContainer>
          <TabContainer dir={'rtl'}>Item Three</TabContainer>
          <TabContainer dir={'rtl'}>Item Four</TabContainer>
        </SwipeableViews>
      </main>
    </div>
  );
}

export default withRouter(Layout_);
