import React from 'react';
import { StateProvider } from './global/state'
import CssBaseline from '@material-ui/core/CssBaseline';
import { ThemeProvider } from '@material-ui/styles';
import { createMuiTheme } from '@material-ui/core/styles';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';

import Layout from './containers/Layout/Layout'
import Login from './containers/Login/Login'

import Ordering from './components/OrderDisplay/Ordering/Ordering'
import OrderReady from './components/OrderDisplay/OrderReady/OrderReady'
import DriverReady from './components/OrderDisplay/DriverReady/DriverReady'


const theme = createMuiTheme({
  palette: {
    primary: {
      main: '#1b5e20',
    },
    secondary: {
      main: '#73c5be',
    },
  },
  status: {
    danger: 'orange',
  },
});

const AppContext = React.createContext();
var initState = {
  appState: {
    logon: false,
    username: ''
  },
  dataState: {

  },
  previousState: {

  }
}

const App = (props) => {
  return (
    <AppContext.Provider value={initState}>
      <React.Fragment>
        <ThemeProvider theme={theme}>
          <CssBaseline />
          <Router>
            <Switch>
              <Route exact path='/ordering'
                render={(props) => <Layout>
                  <Ordering></Ordering>
                </Layout>
                } />
              <Route exact path='/order_ready'
                render={(props) => <Layout>
                  <OrderReady></OrderReady>
                </Layout>
                } />
              <Route exact path='/driver_ready'
                render={(props) => <Layout>
                  <DriverReady></DriverReady>
                </Layout>
                } />
              <Route exact path='/login'
                render={(props) => <Login>
                  <div>login</div>
                </Login>
                } />
            </Switch>
          </Router>
        </ThemeProvider>
      </React.Fragment>
    </AppContext.Provider>
  );
}

export default App;
