import React, {useCallback, useEffect ,useMemo} from 'react';

import { AddBox, ArrowUpward } from "@material-ui/icons";
import { Button } from '@material-ui/core'
import Chip from '@material-ui/core/Chip';
import { makeStyles } from '@material-ui/core/styles';
import MaterialTable from 'material-table';
import axios from './../../../api/axiosClient';
import {useStateValue} from './../../../global/state'


const useStyles = makeStyles(theme => ({
    clicked: {
        disabled: true,
    },
    button: {
        margin: theme.spacing(0.5),
    },
    chip: {
        margin: theme.spacing(0.5),
        backgroundColor: '#e31b2f',
        color: 'white'
    }
}));

const Cooking = (props) => {
    const classes = useStyles();
    const columnMetaData = [
        /*
        {
            title: 'Action',
            field: 'action',
            render: props => (
                <div>
                    <Button
                        variant="contained"
                        color="primary"
                        className={classes.button}
                        onClick={(e,rowData) => {
                            console.log('cooking button clicked');
                            console.log(e);
                            console.log(rowData);
                        }} //props.onChange(e.target.value)
                    >
                        Ready to pickup
                    </Button>
                </div>
            ),

            cellStyle: {
                padding: '3px 1px 3px 1px',
            }

        },
        */
        {
            title: 'Status',
            field: 'status',
            cellStyle: {
                //backgroundColor: '#e31b2f',
                //color: 'white'
                textAlign: 'center',
                padding: '1px 1px 1px 1px',
            },
            render: props => (
                <div>
                    <Chip label="Cooking" className={classes.chip}  />
                </div>
            ),
        },
        {
            title: 'Order ID',
            field: 'orderName',
            //lookup: { 34: 'İstanbul', 63: 'Şanlıurfa' },

        },
        { title: 'Order Time' ,
            field: 'orderTime',
            type: 'datetime',
            cellStyle: {
                padding: '1px 1px 1px 1px',
            }
        },
        { title: 'Remark',
            field: 'future',
            cellStyle: {
                padding: '1px 1px 1px 1px',
            }
        },
        { title: 'Gross Total',
            field: 'grossTotal',
            type: 'currency' ,
            currencySetting: {
                currencyCode: 'THB'
            }

        },
    ];

    //Local state
    const [intervalId,setIntervalId] = React.useState();

    //Global state
    const [{ appState ,dataState }, dispatch] = useStateValue();

    //Hook use Local state
    const [state, setState] = React.useState({
        columns: columnMetaData,
        data: [
            //{ status: 'cooking', future: '2019-07-17 13:30:15' , orderTime: '2019-07-17 13:00:15',storeName: 'Lab Avani', orderName: 'TPC ref 12345', grossTotal: "323.25", refID: "12345" },
        ],
    });

    //loading data every interval
    useEffect(() => {
        async function freshOrdersInStore() {
            console.group('freshOrdersInStore');
            console.groupEnd();
            let get_url = '/api/v1/store/orders/1112delivery/'+appState.sitegroup+'/'+appState.store
            await axios.get(get_url)
                .then((response)=>{
                    if(response.data){
                        var results = response.data;
                        console.group(get_url);
                        console.log('API return :');
                        console.log(results);
                        console.groupEnd();

                        if (results.length > 0 ) {
                            dispatch({
                                type: 'FRESH_ORDER_INSTORE_SUCCESS',
                                data: results
                            });
                        }
                        console.group('StateObject');
                        console.log(appState);
                        console.log(dataState);
                        console.groupEnd();
                    }
                })
                .then(() =>{
                    console.group('Rerender data table list');
                    console.log(dataState.order_instore.length + ' record(s)')

                    //Map data appstate to cooking line
                    var orders = dataState.order_instore.length > 0 ? [...dataState.order_instore] : [];
                    var cookingOrders = orders.filter(order => {
                        return (order.cookingFinishTime === null || order.cookingFinishTime == '')
                            && (order.pickupFinishTime === null || order.pickupFinishTime == '')
                    });
                    console.log(cookingOrders);

                    const cookingRows = cookingOrders.map(row => {
                        var raw =  JSON.parse(row.json);
                        return {
                            status: 'cooking',
                            orderName: row.orderName,
                            orderTime: row.tranDate,
                            future: row.orderType == 0 ? '' : row.dueDate,
                            grossTotal: raw.SDM.GrossTotal
                        }
                    });

                    setState({
                        columns: columnMetaData,
                        data: cookingRows
                    });
                    console.groupEnd();


                    console.group('DataTable StateObject');
                    console.log(state);
                    console.groupEnd();
                })
                .catch((error)=>{
                console.log('API error ,could not get data '+error )
                })
        }

        freshOrdersInStore();
        let id = setInterval(function () {
            freshOrdersInStore();
        },dataState.interval)
        setIntervalId(id);

        return function cleanup() {
            clearInterval(id);
        };
    },[]);

    //Map rows of freshing database to data table list to display
    //useMemo(() => {
    //},[appState.order_instore]);

    /*
    useEffect(() => {
        function handleStatusChange(status) {
          setIsOnline(status.isOnline);
        }

        //[componentWillMount ,componentDidMount ,componentDidUpdate]
        ChatAPI.subscribeToFriendStatus(props.friend.id, handleStatusChange);

        //[componentWillUnmount()]
        // Specify how to clean up after this effect:
        return function cleanup() {
          ChatAPI.unsubscribeFromFriendStatus(props.friend.id, handleStatusChange);
        };
      });

      .MuiTableCell-sizeSmall {
            padding: 1px 1px 1px 1px;
        }
      */

    return (
        <MaterialTable
            title="Cooking"
            columns={state.columns}
            data={state.data}
            actionsColumnIndex="1"
            filtering='true'
            options={{
                padding: 'dense',
                pageSize: 10,
                search: false,
                showTitle: false,
                //rowStyle: { backgroundColor: 'green'},
                rowStyle: rowData => {
                    if(rowData.status === "") {
                        return {backgroundColor: 'red'};
                    }

                    return {};
                }
            }}
            action={[
                {
                    icon: 'save',
                    tooltip: 'Save User',
                    onClick: (event, rowData) => alert("You saved " + rowData.name)
                },
                rowData => ({
                     icon: 'delete',
                     tooltip: 'Ready to Pickup',
                     onClick: (event,rowData) => {
                         console.log('cooking button clicked');
                         console.log(event);
                         console.log(rowData);
                     }
                })
            ]}
            options={{
                actionsColumnIndex: 1
            }}
        />
    );
};

export default Cooking;
