import React, {useCallback, useEffect, useMemo, useState} from 'react';
import Grid from '@material-ui/core/Grid';
import DateFnsUtils from '@date-io/date-fns';
import {
    MuiPickersUtilsProvider,
    KeyboardDatePicker,
} from '@material-ui/pickers';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Select from '@material-ui/core/Select';
import FormHelperText from '@material-ui/core/FormHelperText';
import MenuItem from '@material-ui/core/MenuItem';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import axios from "../../api/axiosClient";
import {useStateValue} from "../../global/state";


const useStyles = makeStyles(theme => ({
    formControl: {
        margin: theme.spacing(2),
        minWidth: 120,
    },
    clicked: {
        disabled: true,
    },
    button: {
        margin: theme.spacing(3),
        whiteSpace: 'nowrap',
        backgroundColor: '#73c5be',
        color: 'white'
    },
    close: {
        padding: theme.spacing(0.5),
    },
}));
const sleep = (milliseconds) => {
    return new Promise(resolve => setTimeout(resolve, milliseconds))
}

const SearchPanel = (props) => {
    const classes = useStyles();
    const [{ dataState }, dispatch] = useStateValue();
    const [selectedStartDate, setSelectedStartDate] = React.useState(new Date('2014-08-18T21:11:54'));
    const [selectedEndDate, setSelectedEndDate] = React.useState(new Date('2014-08-18T21:11:54'));
/*
    const loadSites = useMemo( async () => {
        let get_url = '/api/v1/sites'
        console.group('GET ' + get_url);
        console.groupEnd();
        await axios.get(get_url)
            .then(async (response) => {
                //console.log(response.data);
                if (response.data) {
                    var results = response.data;
                    results = results.map(site => {
                        return {
                            siteNumber: site.siteNumber,
                            siteName: site.siteName
                        };
                    })
                    console.log(results);

                    //dispatch({
                    //    type: 'LOAD_SITES',
                    //    data: results
                    //});

                    console.group('ReturnAPI :' + get_url);
                    await setSites([...results]);
                    console.log(sites);
                    console.groupEnd();

                    //return results;
                }
                //console.log(sites);
            })
            .catch(
                (error) => {
                    console.group('API Error :');
                    console.log(error);
                    console.groupEnd();
                }
            )
    },[])
*/

/*

    useEffect(() => {
        const loadData = async () => {
            let get_url = '/api/v1/sites'
            console.group('GET ' + get_url);
            console.groupEnd();
            const result = await axios.get(get_url)
            return result;
        };

        const saveSites = async (result) => {
            await setSites(result.data);
            console.group('Data :');
            console.log(result.data);
            console.groupEnd();
        }
        saveSites(loadData());

        console.group('useEffect :');
        console.log(sites);
        console.groupEnd();
    },[])
*/
    const [sites, setSites] = React.useState([]);
    useEffect(() => {
        console.log(typeof props.listbox);
        props.listbox.then(sites => {
            console.group("useEffect :");
            console.log('render :');
            console.log(sites);
            console.groupEnd();
        }).catch(() => null);
        //console.log(props.listbox);

    },[])
    const handleStartDateChange = date => {
        setSelectedStartDate(date);
    };

    const handleEndDateChange = date => {
        setSelectedEndDate(date);
    };

    const [values, setValues] = React.useState({
        age: '',
        name: 'hai',
    });
    const handleChange = event => {
        setValues(oldValues => ({
            ...oldValues,
            [event.target.name]: event.target.value,
        }));
    };



    return <MuiPickersUtilsProvider utils={DateFnsUtils}>
        <Grid container justify="flex-start" spacing={2}>
            <Grid key="0" item>
                <KeyboardDatePicker
                    margin="normal"
                    id="startdate-picker-dialog"
                    label="Start Date"
                    format="MM/dd/yyyy"
                    value={selectedStartDate}
                    onChange={handleStartDateChange}
                    KeyboardButtonProps={{
                        'aria-label': 'change date',
                    }}
                />
            </Grid>
            <Grid key="1" item>
                <KeyboardDatePicker
                    margin="normal"
                    id="startdate-picker-dialog"
                    label="End Date"
                    format="MM/dd/yyyy"
                    value={selectedEndDate}
                    onChange={handleEndDateChange}
                    KeyboardButtonProps={{
                        'aria-label': 'change date',
                    }}
                />
            </Grid>
            <Grid key="2" item>
                <FormControl className={classes.formControl}>
                    <InputLabel htmlFor="store-helper">Store</InputLabel>
                    <Select
                        value={values.age}
                        onChange={handleChange}
                        inputProps={{
                            name: 'age',
                            id: 'store-helper',
                        }}
                    >
                        {

                        }

                    </Select>
                    {/*<FormHelperText>Store selection</FormHelperText>*/}
                </FormControl>
            </Grid>
            <Grid key="3" item>
                <Button variant="contained" color="secondary" className={classes.button}>
                    Search
                </Button>
            </Grid>
        </Grid>
    </MuiPickersUtilsProvider>;
}


export default SearchPanel;
