import React from 'react';
//import classes from './Ordering.css' 

import { AddBox, ArrowUpward } from "@material-ui/icons";
import { Button } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles';
import MaterialTable from 'material-table';

const useStyles = makeStyles(theme => ({
    clicked: {
        disabled: true,
    },
    button: {
        margin: theme.spacing(1),
    },
}));

const Ordering = (props) => {
    const classes = useStyles();

    //Local state

    const [state, setState] = React.useState({
        columns: [
            {
                title: 'Action', field: 'action',
                render: props => (
                    <div>
                        <Button
                            variant="contained"
                            color="primary"
                            className={classes.button}
                            onClick={e => console.log('cooking button clicked')} //props.onChange(e.target.value)
                        >
                            Cooking
                    </Button>
                        <Button
                            variant="contained"
                            color="secondary"
                            className={classes.button}
                            onClick={e => console.log('cooking button clicked')} //props.onChange(e.target.value)
                        >
                            Pickup
                    </Button>
                    </div>
                )
            },
            { title: 'Store', field: 'storeName' },
            { title: 'Order Name', field: 'orderName' },
            { title: 'Gross Total', field: 'grossTotal', type: 'currency' },
            {
                title: 'Reference ID',
                field: 'refID' //,
                //lookup: { 34: 'İstanbul', 63: 'Şanlıurfa' },
            },
        ],
        data: [

            { storeName: 'Lab Avani', orderName: 'TPC ref 12345', grossTotal: "323.25", refID: "12345" },
            /*
            {
              name: 'Zerya Betül',
              surname: 'Baran',
              birthYear: 2017,
              birthCity: 34,
            },
            */
        ],
    });

    return (
        <MaterialTable
            title="Orders in-store"
            columns={state.columns}
            data={state.data}
            padding="dense"
            actionsColumnIndex

        />
    );
};

export default Ordering;
