import React from 'react';
import { StateProvider } from './global/state'
import CssBaseline from '@material-ui/core/CssBaseline';
import { ThemeProvider } from '@material-ui/styles';
import { createMuiTheme } from '@material-ui/core/styles';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';

import Layout from './containers/Layout/Layout'
import Login from './containers/Login/Login'

import Ordering from './components/Store/Ordering/Ordering'
import OrderReady from './components/Store/OrderReady/OrderReady'
import DriverReady from './components/Store/DriverReady/DriverReady'


const theme = createMuiTheme({
  palette: {
    primary: {
      main: '#1b5e20',
    },
    secondary: {
      main: '#73c5be',
    },
  },
  status: {
    danger: 'orange',
  },
});

//const AppContext = React.createContext();
const persistedState = sessionStorage.getItem("appstate")
  ? JSON.parse(sessionStorage.getItem("appstate"))
  : {};

const App = (props) => {
  const initialState = {
    appState: {
      logon: false,
      username: ''
    },
    dataState: {

    },
    previousState: {

    }
  }

  const reducer = (state, action) => {
    switch (action.type) {
      case 'LOGIN_SUCCESS':
        var saved = Object.assign({...state});
        saved.appState.logon = true;
        saved.appState.username = action.data.username;
        return {
          ...saved,
          //theme: action.newTheme
        };

      default:
        return state;
    }
  };

  return (
    <StateProvider initialState={initialState} reducer={reducer}>
      <React.Fragment>
        <ThemeProvider theme={theme}>
          <CssBaseline />
          <Router>
            <Switch>
              <Route exact path='/ordering'
                render={(props) => <Layout>
                  <Ordering></Ordering>
                </Layout>
                } />
              <Route exact path='/order_ready'
                render={(props) => <Layout>
                  <OrderReady></OrderReady>
                </Layout>
                } />
              <Route exact path='/driver_ready'
                render={(props) => <Layout>
                  <DriverReady></DriverReady>
                </Layout>
                } />
              <Route exact path='/login'
                render={(props) => <Login>
                  <div>login</div>
                </Login>
                } />
            </Switch>
          </Router>
        </ThemeProvider>
      </React.Fragment>
    </StateProvider>
  );
}

export default App;
