import React ,{useEffect} from 'react';

import { AddBox, ArrowUpward } from "@material-ui/icons";
import { Button } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles';
import MaterialTable from 'material-table';
import axios from './../../../api/axiosClient';
import {useStateValue} from './../../../global/state'


const useStyles = makeStyles(theme => ({
    clicked: {
        disabled: true,
    },
    button: {
        margin: theme.spacing(0.5),
    },
}));

const Cooking = (props) => {
    const classes = useStyles();
    const [{ appState ,dataState }, dispatch] = useStateValue();

    function freshOrdersInStore() {
        axios.get('/api/v1/1112delivery/orders/instore/SZ')
        .then((response)=>{
            if(response.data){
                var results = response.data;
                console.group('API_order_instore');
                console.log('API return :');
                console.log(results);
                if (results.length > 0) {
                    dispatch({
                        type: 'FRESH_ORDER_INSTORE_SUCCESS',
                        data: results
                    });
                }
                console.groupEnd();

                console.group('DataState');
                console.log(appState);
                console.log(dataState);
                console.groupEnd();
            }
        }).catch((error)=>{
            console.log('API error ,could not sign in')
        })
    }


    //Hook use Local state
    const [state, setState] = React.useState({
        columns: [
            {
                title: 'Action',
                field: 'action',
                render: props => (
                    <div>
                        <Button
                            variant="contained"
                            color="secondary"
                            className={classes.button}
                            onClick={e => console.log('cooking button clicked')} //props.onChange(e.target.value)
                        >
                            Ready to pickup
                        </Button>
                    </div>
                ),
                cellStyle: {
                    padding: '1px 1px 1px 1px',
                }
            },
            {
               title: 'Status',
               field: 'status',
               cellStyle: {
                   backgroundColor: 'red',
                   color: 'white',
                   textAlign: 'center',
                   padding: '1px 1px 1px 1px',
               }
            },
            {
                title: 'Order ID',
                field: 'orderName',
                //lookup: { 34: 'İstanbul', 63: 'Şanlıurfa' },
                cellStyle: {
                    padding: '1px 1px 1px 1px',
                }
            },
            { title: 'Order Time' ,
              field: 'orderTime',
              type: 'datetime'
            },
            { title: 'Remark',
              field: 'future',
              cellStyle: {
                    padding: '1px 1px 1px 1px',
              }
            },
            { title: 'Gross Total',
              field: 'grossTotal',
              type: 'currency' ,
              cellStyle: {
                    padding: '1px 1px 1px 1px',
              }
            },
        ],
        data: [

            { status: 'cooking', orderTime: '2019-07-17 13:00:15',storeName: 'Lab Avani', orderName: 'TPC ref 12345', grossTotal: "323.25", refID: "12345" },
            { status: 'cooking',orderTime: '2019-07-17 13:00:15',storeName: 'Lab Avani', orderName: 'TPC ref 12346', grossTotal: "500.00", refID: "12346" },
            { orderTime: '2019-07-17 13:00:15',storeName: 'Lab Avani', orderName: 'TPC ref 12347', grossTotal: "33.5", refID: "12347" },
            /*
            {
              name: 'Zerya Betül',
              surname: 'Baran',
              birthYear: 2017,
              birthCity: 34,
            },
            */
        ],
    });

    //First time loading
    useEffect(() => {
        //Get order in store,
        //Set to state
        freshOrdersInStore();

    },[]);

    /*
    useEffect(() => {
        function handleStatusChange(status) {
          setIsOnline(status.isOnline);
        }

        //[componentWillMount ,componentDidMount ,componentDidUpdate]
        ChatAPI.subscribeToFriendStatus(props.friend.id, handleStatusChange);

        //[componentWillUnmount()]
        // Specify how to clean up after this effect:
        return function cleanup() {
          ChatAPI.unsubscribeFromFriendStatus(props.friend.id, handleStatusChange);
        };
      });

      .MuiTableCell-sizeSmall {
            padding: 1px 1px 1px 1px;
        }
      */

    return (
        <MaterialTable
            title="Cooking"
            columns={state.columns}
            data={state.data}
            actionsColumnIndex="1"
            filtering='true'
            options={{
                padding: '1px',
                pageSize: 10,
                search: false,
                showTitle: false,
                //rowStyle: { backgroundColor: 'green'},
                rowStyle: rowData => {
                    if(rowData.status === "") {
                        return {backgroundColor: 'red'};
                    }

                    return {};
                }
            }}
            style=""
        />
    );
};

export default Cooking;
