import React from 'react';
import { StateProvider, persistState } from './global/state'
import CssBaseline from '@material-ui/core/CssBaseline';
import { ThemeProvider } from '@material-ui/styles';
import { createMuiTheme } from '@material-ui/core/styles';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';

import Layout from './containers/Layout/Layout'
import Login from './containers/Login/Login'

import Ordering from './components/Store/Ordering/Ordering'
import OrderReady from './components/Store/OrderReady/OrderReady'
import DriverReady from './components/Store/DriverReady/DriverReady'


const theme = createMuiTheme({
  palette: {
    primary: {
      main: '#1b5e20',
    },
    secondary: {
      main: '#73c5be',
    },
  },
  status: {
    danger: 'orange',
  },
});

//const AppContext = React.createContext();

const App = (props) => {
  const initialState = {
    appState: {
      logon: false,
      username: ''
    },
    dataState: {},
    previousState: {}
  }

  const persistedState = sessionStorage.getItem("sessionstate")
    ? JSON.parse(sessionStorage.getItem("sessionstate"))
    : initialState;

  const reducer = (state, action) => {
    console.group('Reducer :');
    console.log('action : ');
    console.log(action);
    switch (action.type) {
      case 'LOGIN_SUCCESS':
        var saved = Object.assign({ ...state });
        saved.appState.logon = true;
        saved.appState.username = action.data.username;
        persistState(saved);
        return {
          ...saved,
          //theme: action.newTheme
        };

      default:
        return state;
    }
    console.groupEnd();
  };

  const checkForAuth = (props) => {
    console.log('log on state : ' + persistedState.appState.logon);

    return (
      <Switch>
        <Route exact path='/ordering'
          render={(props) => <Layout>
            <Ordering></Ordering>
          </Layout>
          } />
        <Route exact path='/order_ready'
          render={(props) => <Layout>
            <OrderReady></OrderReady>
          </Layout>
          } />
        <Route exact path='/driver_ready'
          render={(props) => <Layout>
            <DriverReady></DriverReady>
          </Layout>
          } />
        <Route render={(props) => {
          if (persistedState.appState.logon) {
            return (
              <Layout>
                <Ordering></Ordering>
              </Layout>);
          }
          return (
            <Login>
              <div>login</div>
            </Login>
          );

        }
        } />
      </Switch>
    );
  };

  return (
    <StateProvider initialState={persistedState} reducer={reducer}>
      <React.Fragment>
        <ThemeProvider theme={theme}>
          <CssBaseline />
          <Router>
            {checkForAuth(props)}
          </Router>
        </ThemeProvider>
      </React.Fragment>
    </StateProvider>
  );
}

export default App;
