import React, {useCallback, useEffect, useMemo, useState} from 'react';
import Grid from '@material-ui/core/Grid';
import DateFnsUtils from '@date-io/date-fns';
import {
    MuiPickersUtilsProvider,
    KeyboardDatePicker,
} from '@material-ui/pickers';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Select from '@material-ui/core/Select';
import FormHelperText from '@material-ui/core/FormHelperText';
import MenuItem from '@material-ui/core/MenuItem';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import axios from "../../api/axiosClient";


const useStyles = makeStyles(theme => ({
    formControl: {
        margin: theme.spacing(2),
        minWidth: 120,
    },
    clicked: {
        disabled: true,
    },
    button: {
        margin: theme.spacing(3),
        whiteSpace: 'nowrap',
        backgroundColor: '#73c5be',
        color: 'white'
    },
    close: {
        padding: theme.spacing(0.5),
    },
}));

const SearchPanel = (props) => {
    const classes = useStyles();
    const [selectedStartDate, setSelectedStartDate] = React.useState(new Date('2014-08-18T21:11:54'));
    const [selectedEndDate, setSelectedEndDate] = React.useState(new Date('2014-08-18T21:11:54'));
    const [sites, setSites] = React.useState([]);

    const handleStartDateChange = date => {
        setSelectedStartDate(date);
    };

    const handleEndDateChange = date => {
        setSelectedEndDate(date);
    };

    const [values, setValues] = React.useState({
        age: '',
        name: 'hai',
    });
    const handleChange = event => {
        setValues(oldValues => ({
            ...oldValues,
            [event.target.name]: event.target.value,
        }));
    };

    useEffect(() => {
        let get_url = '/api/v1/sites'
        console.group('GET '+get_url);
        axios.get(get_url)
                .then((response)=>{
                    console.log(response.data);
                    if(response.data){
                        var results = response.data;
                        results = results.map(site => {
                            return {
                                siteNumber: site.siteNumber,
                                siteName: site.siteName
                            };
                        })
                        console.log(results);
                        setSites([...results]);
                    }
                    console.log(sites);
                })
                .catch(
                    (error)=>{
                        console.group('API Error :');
                        console.log(error);
                        console.groupEnd();
                    }
                )
        console.groupEnd();
    },[])

    return <MuiPickersUtilsProvider utils={DateFnsUtils}>
        <Grid container justify="flex-start" spacing={2}>
            <Grid key="0" item>
                <KeyboardDatePicker
                    margin="normal"
                    id="startdate-picker-dialog"
                    label="Start Date"
                    format="MM/dd/yyyy"
                    value={selectedStartDate}
                    onChange={handleStartDateChange}
                    KeyboardButtonProps={{
                        'aria-label': 'change date',
                    }}
                />
            </Grid>
            <Grid key="1" item>
                <KeyboardDatePicker
                    margin="normal"
                    id="startdate-picker-dialog"
                    label="End Date"
                    format="MM/dd/yyyy"
                    value={selectedEndDate}
                    onChange={handleEndDateChange}
                    KeyboardButtonProps={{
                        'aria-label': 'change date',
                    }}
                />
            </Grid>
            <Grid key="2" item>
                <FormControl className={classes.formControl}>
                    <InputLabel htmlFor="store-helper">Store</InputLabel>
                    <Select
                        value={values.age}
                        onChange={handleChange}
                        inputProps={{
                            name: 'age',
                            id: 'store-helper',
                        }}
                    >
                        <MenuItem value="">
                            <em>None</em>
                        </MenuItem>
                        {sites.forEach(site =>
                            <MenuItem value={site.siteNumber}>{site.siteName}</MenuItem>
                        )}
                    </Select>
                    {/*<FormHelperText>Store selection</FormHelperText>*/}
                </FormControl>
            </Grid>
            <Grid key="3" item>
                <Button variant="contained" color="secondary" className={classes.button}>
                    Search
                </Button>
            </Grid>
        </Grid>
    </MuiPickersUtilsProvider>;
}


export default SearchPanel;
